let naruto;
// In case you want to use this
function getJson(url) {
    return fetch(url)
    .then(res => res.json())
    .then((out) => {
        return out;
    })
    .catch(err => { throw err });
}

// Just a simple recursion to store the connections and data in the array
// 0 will be the nodes_data and 1 will be the nodes_links
function populateMetadata(array, parent, currentObject) {
    if (currentObject != null) {
        array[0].push({'id': currentObject.id, 'screen_name': currentObject.screen_name, 'text': currentObject.text,
            'num_replies': currentObject.num_replies, 'sentiment': currentObject.sentiment, 
            'replies': currentObject.replies,'num_retweet': currentObject.num_retweet,
            'num_fav': currentObject.num_fav});
        if (parent != null) {
            array[1].push({'source': parent.id, 'target': currentObject.id});
        }
        for (let i = 0; i < currentObject.num_replies; i++) {
            populateMetadata(array, currentObject, currentObject.replies[i]);
        }
    }
}

let graphMetadata = [[],[]];

populateMetadata(graphMetadata, null, testjson);

console.log(graphMetadata);

node_data = graphMetadata[0];
node_links = graphMetadata[1];

// -------------------- Simulation ---------------------------
function tickActions() {
    //update link positions
    //simply tells one end of the line to follow one node around
    //and the other end of the line to follow the other node around
    link
        .attr("x1", function(d) { return d.source.x; })
        .attr("y1", function(d) { return d.source.y; })
        .attr("x2", function(d) { return d.target.x; })
        .attr("y2", function(d) { return d.target.y; });

    //update circle positions to reflect node updates on each tick of the simulation
    node
        .attr("cx", function(d) { return d.x; })
        .attr("cy", function(d) { return d.y; });
}

function circleColour(d){
    // TODO Create a function to give an average of the sentiments
    let sentiment = d.sentiment;

    if(sentiment < -0.05) {
        let green = 255 * (1+sentiment);
        return "rgb(255," + green + ",0)";
    } else {
        let red = 255 * (1-sentiment);
        return "rgb("+red+",255, 0)";
    } /*else {
        return "gray";
    }*/
}

// Calculate Height of the tree. " gk -> paper variable."
function maxPathLenthOfGraph(d){
	let maximumLength =0;
	let aux=0;
	console.log(typeof(aux));
	for (let i=0;i<d.num_replies;i++){
		console.log(typeof(d.replies));
		aux= maxPathLenthOfGraph(d.replies[i]);
		maximumLength = Math.max(aux, maximumLength);
	}
	return maximumLength + 1;
}

//Number of childs of a given root. "c" -> paper variable
function numberRootChilds(d){
	return d.num_replies;
}

//Number of nodes. "nk" -> paper variable
function totalNumberNodes(d){
	let result = 1; // current node
	for (let i=0;i<d.num_replies;i++){
		result += totalNumberNodes(d.replies[i]);
	}
	return result;
}

//Energy of retweets
// All retweets of given tree.
function energyRT(d){
	let result = d.num_retweet;	
	for (let i=0;i<d.num_replies;i++){
		result += energyRT(d.replies[i]);
	}
	return result;
}

//Energy of favs.
// All favs of given tree.
function energyFAV(d){
	let result = d.num_fav;
	for (let i=0;i<d.num_replies;i++){
		result += energyFAV(d.replies[i]);
	}
	return result;
}

function getRepliesSentiment(d) {
    if (d.id === testjson.id) {
        let overallSentiment = recursiveTotalSentiment(d) / totalNumberNodes(d);
        console.log("Replies Sentiment --> " + overallSentiment);
        let thing = {
            sentiment: overallSentiment
        };
        let naruto = circleColour(thing);
        console.log(naruto);
        return naruto;
    }
}

function getStrokeWidth(d) {
    if (d.id === testjson.id) {
        return 3;
    }
}

function recursiveTotalSentiment(d) {
    let result = d.sentiment;
    for (let i = 0; i < d.num_replies; i++) {
        result += recursiveTotalSentiment(d.replies[i]);
    }
    return result;
}


function size(d) {
	let lambda1=1;
	let lambda2=0;
	let lambda3=0;
    // TODO Temporal size
    // just E_replies by now
    return  3+0.02*(lambda1*(maxPathLenthOfGraph(d)*(numberRootChilds(d)+totalNumberNodes(d)))+
	lambda2*energyRT(d)+ 
	lambda3*energyFAV(d))
    ;
}

function drag_start(d) {
  if (!d3.event.active) simulation.alphaTarget(0.3).restart();
  d.fx = d.x;
  d.fy = d.y;
}

function drag_drag(d) {
  d.fx = d3.event.x;
  d.fy = d3.event.y;
}

function drag_end(d) {
  if (!d3.event.active) simulation.alphaTarget(0);
  // Just send them back to where they were
  d.fx = d.x;
  d.fy = d.y;
}

let svg = d3.select("svg"),
 width = +svg.attr("width"),
 height = +svg.attr("height");


// Setup simulation
// Nodes only for now
let simulation = d3.forceSimulation().nodes(node_data);

//Create the link force
//We need the id accessor to use named sources and targets
let link_force =  d3.forceLink(node_links)
                        .id(function(d) { return d.id; });
link_force.distance(60);
simulation.force("links",link_force);

//draw lines for the links
let link = svg.append("g")
      .attr("class", "links")
    .selectAll("line")
    .data(node_links)
    .enter().append("line")
      .attr("stroke-width", 1);

forceBodies = d3.forceManyBody();
