let tip;
  svg.on("click", function(){
    if (tip) tip.remove();
  });
  node.on("click", function(d){
    d3.event.stopPropagation();

    if (tip) tip.remove();

    tip  = svg.append("g")
      .attr("transform", "translate(" + d.x  + "," + d.y + ")");

    let rect = tip.append("rect")
      .style("fill", "white")
      .style("stroke", "steelblue");

    tip.append("text")
      .text("Screen Name: " + d.screen_name)
      .attr("dy", "1em")
      .attr("x", 5);

    tip.append("text")
      .text("Text: " + d.text)
      .attr("dy", "2em")
      .attr("x", 5);

    tip.append("text")
      .text("Likes: " + d.num_fav)
      .attr("dy", "3em")
      .attr("x", 5);

    let bbox = tip.node().getBBox();
    rect.attr("width", bbox.width + 5)
        .attr("height", bbox.height + 5)
  });

